mapper.select:
select * from(
select a.*, rownum rn from
 (select * from board 

 order by num desc) a
 ) 		 
 where rn between #{start} and #{end}

mapper.update:
update board set subject=#{subject}, content=#{content:VARCHAR},
	wdate=systimestamp

	, filename=#{filename}, filesize=#{filesize}

where num=#{num}
